mapper.select:
select
  FILE_ID, FILE_NAME, FILE_EXT, FILE_TYPE, SIZES, PATH, READ_PATH, CREATE_TIME, CREATOR, UPDATE_TIME, UPDATOR, GROUP_ID, LEVELS
from ZZ_GROUP_FILE


        and FILE_ID = #{fileId}


        and FILE_NAME = #{fileName}


        and FILE_EXT = #{fileExt}


        and FILE_TYPE = #{fileType}


        and SIZES = #{sizes}


        and PATH = #{path}


        and READ_PATH = #{readPath}


        and CREATE_TIME = #{createTime}


        and CREATOR = #{creator}


        and UPDATE_TIME = #{updateTime}


        and UPDATOR = #{updator}


        and GROUP_ID = #{groupId}


        and LEVELS = #{levels}

mapper.update:
update ZZ_GROUP_FILE
set SEND_FLG='1',
LEVELS=#{param.levels},
GROUP_ID=#{param.receiver},
APPROVE_FLG=#{param.approveFlg}
where FILE_ID=#{param.fileId}

mapper.select:
select f.file_id, f.file_name || decode(nvl(f.file_ext, 'null'), 'null', '', '.' || f.file_ext) as file_name, TO_CHAR(nvl(f.sizes, 0) / 1024 / 1024,'FM999999999990.00') as file_size, f.path as file_path, to_char(f.create_time,'yyyy-mm-dd hh24:mi:ss') as upload_time, f.levels, f.creator_name, (case when group_id in ( select group_id from zz_group ) then '1' else '0' END) is_group from zz_group_file f where f.send_flg = '1'
and f.file_name || decode(nvl(f.file_ext, 'null'), 'null', '', '.' || f.file_ext) like '%'||#{params.fileName}||'%'
and f.creator_name like '%'||#{params.userName}||'%'
and f.levels = #{params.level}
and to_char(create_time, 'yyyy-mm-dd') >= #{params.dateBegin}
and to_char(create_time, 'yyyy-mm-dd') <= #{params.dateEnd}
and exists (select 1 from zz_group g where g.group_id=f.group_id)
and not exists (select 1 from zz_group g where g.group_id=f.group_id)

update.foreach:
update ZZ_GROUP_FILE
set update_time=sysdate,
updator=#{param.updator},
approve_flg=#{param.approveFlg}
where FILE_ID=#{param.fileId}
